import {NavLink}            from 'react-router-dom';
import homePage             from './homePage.css'; 

class HomePage extends Component {
    constructor(props) {
        super(props);
        console.log("in bulles ?", props.days)
        this.state = {  };
        this.lastElementofURL = this.findLastElement(this.props.url);
    }

    findLastElement = (url) => {
        const splitted = url.split('/a');
        let lastSegment = splitted.pop();
    return lastSegment
}

    render() { 
        return ( 
            <div className="headerHomePage">
                <div className="daysCalendar">
                    {
                    this.props.days.map((day, index) => 
                    <div className="daysCalendarLink">
                        <NavLink  
                            className="dayCalendarLink" 
                            activeClassName="active" 
                            to={`/Bulle/${index + 1}`}
                        >
                            <img src={this.props.days[this.lastElementofURL].customisationPersonnelle} className="homePageP">Day {index+1}</img>
                        </NavLink>
                        <div className="buttonLike">
                            <button onClick={() => this.props.selectLike(index)} >Like</button>
                                { this.props.days.clicked ? <div>C'est cliked !</div> : null}
                        </div>
                    </div>
                    )
                    }
                </div>
            </div>
        )
    }
}

export default HomePage;


