import React, { Component } from 'react';
import                           './App.css';
import { Switch, Route }    from 'react-router-dom';
import Bulle                from './components/Bulle/Bulle';
import HomePage             from './components/HomePage/HomePage'

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {

            likes: 124,
            customisationPersonnelle : 'Ceci est ma customisation',
            prixAssociation : 5,
            asblInfo : 'Ceci est une présentation de l\'asbl',
            clicked: false 
         }
    }

    handleClickToLike = () => {
        this.setState({clicked : true});
    }

    render() { 
    return ( 
        <div>
            <Switch>
                <Route
                    exact 
                    path='/'
                    render={(props) => < HomePage
                    selectLike={() => this.handleClickToLike()}
                    component={HomePage} />}
                />
                <Route 
                    path='/Bulle'
                    {...this.state}
                    render={(props) => < Bulle
                    likes={this.state.selectLikes}
                    customisationPersonnelle={this.state.customisationPersonnelle}
                    prixAssociation={this.state.prixAssociation}
                    asblInfo={this.state.asblInfo} />
                    }
                />
            </Switch>
        </div>
    );
  }
}

export default App;
