import React, { Component } from 'react';
import {NavLink}            from 'react-router-dom';
import homePage             from './homePage.css'; 

class HomePage extends Component {
    constructor(props) {
        super(props);
        console.log("in bulles ?", props.days)
        this.state = { 
            image: "https://cdn.pixabay.com/photo/2016/01/19/17/17/christmas-tree-1149619_960_720.jpg", 

        };
        // this.lastElementofURL = this.findLastElement(this.props.url);
    }

    // findLastElement = (url) => {
    //     const splitted = url.split('/');
    //     let lastSegment = splitted.pop();
    // return lastSegment
// }


    render() { 
        return ( 
            <div className="headerHomePage">
                <div className="daysCalendar">
                    {
                    this.props.days.map((day, index) => 
                    <div className="daysCalendarLink">
                        <NavLink  
                            className="dayCalendarLink" 
                            activeClassName="active" 
                            to={`/Bulle/${index + 1}`}
                        >
                            <img src="https://cdn.pixabay.com/photo/2016/01/19/17/17/christmas-tree-1149619_960_720.jpg" width="100%" height="100%" ></img>
                            <p className="homePageP">Day {index+1}</p>
                        </NavLink>
                        <div className="buttonLike">
                            <button onClick={() => this.props.selectLike(index)} >Like</button>
                        </div>
                    </div>
                    )
                    }
                </div>
            </div>
        )
    }
}

export default HomePage;


