import React, { Component } from 'react';
import './App.css';
import Bulle from './components/Bulle/Bulle';



class App extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            likes: 124,
            customisationPersonnelle : 'Ceci est ma customisation',
            prixAssociation : 5,
            asblInfo : 'Ceci est une présentation de l\'asbl',
         }
    }



    render() { 
    return ( 
        <div>
            <Bulle 
                likes={this.state.likes}
                customisationPersonnelle={this.state.customisationPersonnelle}
                prixAssociation={this.state.prixAssociation}
                asblInfo={this.state.asblInfo}
            />
        </div>
    );
  }
}

export default App;

<div className="AppAdventCalendar">
          <Switch>
            <Route
              exact
              path='/'
              render={(props) => <Header 
                selectRuler={() => this.handleClickToCreateGame()} />}
            />
            <Route 
              path="/Avatar" 
              render={(props) => <Avatar 
              avatar={this.state.avatar}
              pseudo={this.state.pseudo}

              selectImageAvatar={(image) => this.handleClick(image)} 
              selectNameAvatar={(event) => this.myChangeName(event)}
              selectIdAvatar={(event) => this.sendUserToGamePage(event)}

              isDrawer={this.state.isDrawer} />}>
            </Route>


            <Route path='/Feuille1' 
            component={Feuille1}></Route>

            <Route path='/Feuille2' 
            component={Feuille2}></Route>

            <Route 
              path="/GameDrawer" 
              render={(props) => 
                <Game user={this.state} />}>
            </Route>
          </Switch>       
      </div>
